import { Navigation, Pagination, Scrollbar, A11y } from 'swiper';

import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

export default () => {
  return (
    <div className='slider'>
    <Swiper
      // install Swiper modules
      modules={[Navigation, Pagination, Scrollbar, A11y]}
      spaceBetween={50}
      slidesPerView={3}
      navigation={true}
      pagination={{ clickable: true }}
      scrollbar={{ draggable: true }}
      onSwiper={(swiper) => console.log(swiper)}
      onSlideChange={() => console.log('slide change')}
    >

        <div className='slider-heading'>
      <SwiperSlide className='swiper-slide'>
      <div className='swiper-slide-img-wrapper'>
                        <img src="images/products/1.jpg" className="swiper-slide-img"  />
                        <img  src="images/products/2.jpg" className="swiper-slide-img"  />
                        <img  src="images/products/3.jpg" className="swiper-slide-img"  />
                        <img src="images/products/4.jpg" className="swiper-slide-img"  />
                        <img  src="images/products/5.jpg" className="swiper-slide-img"  />
                        <img src="images/products/6.jpg" className="swiper-slide-img"  />
                     </div>


      </SwiperSlide>
      <SwiperSlide className='swiper-slide'>
      <div className='swiper-slide-img-wrapper'>
                        <img src="images/products/1.jpg" className="swiper-slide-img"  />
                        <img  src="images/products/2.jpg" className="swiper-slide-img"  />
                        <img  src="images/products/3.jpg" className="swiper-slide-img"  />
                        <img src="images/products/4.jpg" className="swiper-slide-img"  />
                        <img  src="images/products/5.jpg" className="swiper-slide-img"  />
                        <img src="images/products/6.jpg" className="swiper-slide-img"  />
                     </div>


      </SwiperSlide>

      </div>


    </Swiper>

    </div>
  );
};
