import './SubHeader.css';
import MenuIcon from '@mui/icons-material/Menu';
import React, {useState} from 'react';
import {Transition} from 'react-transition-group';


function SubHeader() {
  const [navOpen, setNavOpen] = useState(false);
 

  const openNav = () => {
      setNavOpen(true);
  }

  const closeNav = () => {
    setNavOpen(false);
    
  }
  return (
    <div className="sub-nav">

      <div className="left">
      
        <a href='' className="left-item all">
        
          <MenuIcon  click={openNav} id="hamburger" /> All
        </a>
        <Transition
      in={navOpen && entryStore}
      timeout={300}
      mountOnEnter
      unmountOnExit>
      {state => {
        if(state === "exited")  
        return(
        <>
        <Sidenav state={state} />
        <div className="overlay" 
        style={state === "entering" ? {animation: "show .3s forwards"} 
        : state === "entered" ? {opacity: "1"} : {animation: "show .3s backwards reverse"}}
        onClick={closeNav}></div>
        <div className="closeBtn" style={state === "entering" ? {animation: "show .3s forwards"} 
        : state === "entered" ? {opacity: "1"} : {animation: "show .3s backwards reverse"}}
        onClick={closeNav}>&times;</div>
      </>
      )}}
      </Transition>
       
        <a href='' className="left-item">
          Today's Deals
        </a>
        <a href='' className="left-item">
          Customer Services
        </a>
        
        <a href='' className="left-item">
          Registry
        </a>
        <a href='' className="left-item">
         Gift Cards
        </a>
        <a href='' className="left-item">
         Sell
        </a>
      
      </div>

      

    </div>
  )
}

export default SubHeader
